import styles from './ProductForm.module.scss';
import clsx from 'clsx';

const ProductForm = (props) => {
    return (

        <form onSubmit={props.handleSubmit}>
          <div className={styles.sizes}>
            <h3 className={styles.optionLabel}>Sizes</h3>
            <ul className={styles.choices}>
              {props.sizes.map((size, index) => 

              <li key={index}>
                <button type="button" onClick={() => {
                  props.handleSetSize(size.name);
                  props.getPrice(size.additionalPrice);
                }}
                className={clsx(size.name === props.currentSize && styles.active)}>
                {size.name}</button>
              </li>)}

            </ul>
          </div>
          <div className={styles.colors}>
            <h3 className={styles.optionLabel}>Colors</h3>
            <ul className={styles.choices}>

              {props.colors.map(item => <li key={item}>
                <button type="button" 
                className={clsx(props.prepareColorClassName(item), item === props.currentColor && styles.active)} 
                onClick={() => handleSetColor(item)}/>
              </li>)}

            </ul>
          </div>

          <Button 
            type="submit"
            className={styles.button}>
            <span className="fa fa-shopping-cart" />
          </Button>
        </form>

    );
};

export default ProductForm;
